INSERT INTO ayat (aya_text, aya_tafsir, audiourl, aya_number, surah_id)
VALUES
    ('بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ', 'باسـم الله أبدأ قراءة القرآن، مستعيـــنًا به تعالى مـتبركًا بذكر اسمه. وقد تضمنت البسملة ثلاثة من أسماء الله الحسنى، وهي: 1 - «الله»؛ أي: المعبود بحق، وهو أخص أسماء الله تعالى، ولا يسمى به غيره سبحانه. 2 - «الرَّحْـمَن»؛ أي: ذو الرحمة الواسعة. فهو الرحمن بذاته. 3 - «الرَّحِيم»؛ أي: ذو الرحمة الواصلة. فهو يرحم برحمته من شاء من خلقه ومنهم المؤمنون من عباده.', 'https://verses.quran.com/AbdulBaset/Mujawwad/mp3/001001.mp3', 1, 2395),
    ('الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ', 'الثناء الكامل، وجميع أنواع المحامد من صفات الجلال والكمال هي لله وحده دون من سواه؛ إذ هو رب كل شيء وخالقه ومدبره. والعالمون جمع عالَم، وهم كل ما سوى الله تعالى.', 'https://verses.quran.com/AbdulBaset/Mujawwad/mp3/001002.mp3', 2, 2395),
    ('الرَّحْمَٰنِ الرَّحِيمِ', 'ثناء على الله تعالى بأنه الرحمن الرحيم بعد حمده في الآية السابقة.', 'https://verses.quran.com/AbdulBaset/Mujawwad/mp3/001003.mp3', 3, 2395),
    ('مَالِكِ يَوْمِ الدِّينِ', 'تمجيد لله تعالى بأنه المالك لكل ما في يوم القيامة، حيث لا تملك نفس لنفس شيئًا. ف«يوم الدين»: يوم الجزاء والحساب.', 'https://verses.quran.com/AbdulBaset/Mujawwad/mp3/001004.mp3', 4, 2395),
    ('إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ', 'نخصُّك وحدك بأنواع العبادة والطاعة، فلا نشرك معك غيرك، ومنك وحدك نطلب العون في كل شؤوننا، فبِيَدِكَ الخير كله، ولا مُعين سواك.', 'https://verses.quran.com/AbdulBaset/Mujawwad/mp3/001005.mp3', 5, 2395),
    ('اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ', 'دُلَّنا إلى الصراط المستقيم، واسلك بنا فيه، وثبِّتنا عليه، وزدنا هدى. و(الصراط المستقيم) هو الطريق الواضح الذي لا اعوجاج فيه، وهو الإسلام الذي أرسل الله به محمدًا صلى الله عليه وسلم.', 'https://verses.quran.com/AbdulBaset/Mujawwad/mp3/001006.mp3', 6, 2395),
    ('صِرَاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ ...', 'طريق الذين أنعمت عليهم من عبادك بهدايتهم؛ كالنبيين والصدِّيقين والشهداء والصالحين وحَسُنَ أولئك رفيقًا، غير طريق المغضوب عليهم الذين عرفوا الحق ولم يتبعوه كاليهود، وغير طريق الضالين عن الحق الذين لم يهتدوا إليه لتفريطهم في طلب الحق والاهتداء إليه كالنصارى.', 'https://verses.quran.com/AbdulBaset/Mujawwad/mp3/001007.mp3', 7, 2395);
